import React, { Component } from "react";
import logo from "../Images/logo.svg";
//import ScrollIntoView from "react-scroll-into-view";
import List from "./List";
import LeafletMap from "./LeafletMap";
////search/fashion-brand/
//https://www/google.ca

class Search extends Component {
  state = {
    value: "",
    valid1: 0,
    valid2: 0,
    city: ""
  };

  constructor() {
    super();
    this.handleChange = this.handleChange.bind(this);
    this.handleChangeText = this.handleChangeText.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleSubmitCity = this.handleSubmitCity.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleChangeText(event) {
    this.setState({ city: event.target.city });
  }

  handleSubmit(event) {
    event.preventDefault();

    const isImageUrl = require("is-image-url");

    if (isImageUrl(this.state.value)) {
      this.setState({ valid1: 1 });
    } else {
      this.setState({ valid1: -1 });
    }
  }

  handleSubmitCity(event) {
    event.preventDefault();

    //submit to server, based on response
    if (false) {
      this.setState({ valid2: 1 });
    } else {
      this.setState({ valid2: -1 });
    }
  }

  render() {
    return (
      <React.Fragment>
        <img
          id="logo"
          src={logo}
          width="25%"
          height="20%"
          className="App-logo"
          alt="react logo"
        />
        <form onSubmit={this.handleSubmit}>
          <div className="input-group mb-3">
            <input
              id="imageurl"
              name="imageurl"
              type="url"
              onChange={this.handleChange}
              value={this.state.value}
              className="form-control"
              placeholder="Image URL"
              required="required"
            />

            <input type="submit" className="btn btn-dark" value="Search" />
          </div>
        </form>
        {this.state.valid1 !== 0 &&
          (this.state.valid1 === -1 ? (
            <div className=" message btn bg-danger"> Invalid Image </div>
          ) : (
            <div>
              <form onSubmit={this.handleSubmitCity}>
                <List />

                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    required="required"
                    onChange={this.handleChangeCity}
                    value={this.state.city}
                  />
                </div>
                <input
                  type="submit"
                  className="btn btn-dark m-2"
                  value="Submit"
                />
              </form>
            </div>
          ))}
        {this.state.valid2 !== 0 &&
          (this.state.valid2 === -1 ? (
            <div className=" message btn bg-danger"> Imvalid City </div>
          ) : (
            <div>
              <LeafletMap />
            </div>
          ))}
      </React.Fragment>
    );
  }
}

export default Search;
