import React, { Component } from "react";

class Tag extends Component {
  state = { label: this.props.label, checked: this.props.checked };

  handleChangeRadio = () => {
    this.props.onSelectRadio(this.stae.value);
  };

  render() {
    return (
      <div className="input-group">
        <div className="input-group-prepend">
          <div className="input-group-text">
            <input
              name="tags"
              type="radio"
              id={this.state.label}
              onChange={this.handleChangeRadio}
              required="required"
            />
          </div>
        </div>
        <label type="text" className="form-control" htmlFor={this.state.label}>
          {this.state.label}
        </label>
      </div>
    );
  }
}

export default Tag;
