import React, { Component } from "react";
import logo from "../Images/logo.svg";
//import ScrollIntoView from "react-scroll-into-view";
import List from "./List";
////search/fashion-brand/
//https://www/google.ca

class Search extends Component {
  state = {
    value: "",
    valid: 0,
    city: "",
    cityList: [
      "Windsor",
      "Toronto",
      "Vancouver",
      "Hong Kong",
      "Japan",
      "London",
      "New York"
    ],
    allCities: [
      "Windsor",
      "Toronto",
      "Vancouver",
      "Hong Kong",
      "Japan",
      "London",
      "New York"
    ]
  };

  getCities() {
    const citiesJSON = require("all-the-cities");
    console.log("Cities", this.citiesJSON);

    let tempCitiies = [];

    for (var i = 0; i < citiesJSON.length; i++) {
      tempCitiies[i] = citiesJSON[i]["name"];
    }

    this.setState({ allCities: tempCitiies });
  }

  constructor() {
    super();
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCityChange = this.handleCityChange.bind(this);
    this.handleSubmit2 = this.handleSubmit2.bind(this);

    //this.getCities();
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();

    const isImageUrl = require("is-image-url");

    if (isImageUrl(this.state.value)) {
      this.setState({ valid: 1 });
    } else {
      this.setState({ valid: -1 });
    }
  }

  handleCityChange(event) {
    this.setState({
      cityList: this.allCities.slice(0, 10).filter(city => {
        return city.name.match(event.target.city);
      })
    });
    console.log(this.state.cityList);
  }

  handleSubmit2(event) {
    event.preventDefault();
  }

  render() {
    return (
      <React.Fragment>
        <img
          id="logo"
          src={logo}
          width="25%"
          height="20%"
          className="App-logo"
          alt="react logo"
        />
        <form onSubmit={this.handleSubmit}>
          <div className="input-group mb-3">
            <input
              id="imageurl"
              name="imageurl"
              type="url"
              onChange={this.handleChange}
              value={this.state.value}
              className="form-control"
              placeholder="Image URL"
              required="required"
            />

            <input type="submit" className="btn btn-dark" value="Search" />
          </div>
        </form>
        {this.state.valid !== 0 &&
          (this.state.valid === -1 ? (
            <div className=" message btn bg-danger"> Invalid Image </div>
          ) : (
            <div>
              <form onSubmit={this.handleSubmit2}>
                <List />

                <div className="input-group">
                  <input type="text" className="form-control" />
                  <div className="input-group-append">
                    <select className="custom-select">
                      {this.state.cityList.map(city => (
                        <option value={city}>{city}</option>
                      ))}
                    </select>
                  </div>
                </div>
                <input type="submit" className="btn btn-dark" value="Submit" />
              </form>
            </div>
          ))}
      </React.Fragment>
    );
  }
}

export default Search;
